import React, { useCallback, useContext, useState, useEffect } from 'react';
import { useTranslation } from 'react-i18next';

import { InputTextController } from '../../../components/PromotionComponent/InputTextController';
import { DropdownController } from '../../../components/PromotionComponent/DropdownController';

import { CommonService } from '../../../service/CommonService';
// import { UomService } from '../../../service/UomService';

import { UserProfileContext } from '../../../App';

export const GeneralInformation = (props) => {

    const { control, errors } = props;
    const [t] = useTranslation('common');
    const userProfile = useContext(UserProfileContext);
    const commonService = new CommonService();
    // const uomService = new UomService();

    const [listProductStoreId, setListProductStoreId] = useState([]);
    // const [currencies, setCurrencies] = useState([]);

    const emptyList = [];

    useEffect(() => {
        init();
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const init = useCallback(async () => {
        let params = {
            enumTypeId: 'PartyType'
        }
        let data = await commonService.getEnumeration(params);

        data = await commonService.getProductStores(userProfile.info.partyId);
        setListProductStoreId(data.productStoreList.map((elm) => {
            return {
                name: elm.productStoreId,
                code: elm.productStoreId
            }
        }));
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const getFormErrorMessage = (name) => {
        return errors[name] && <small className="p-error">{errors[name]?.message}</small>
    };

    return (
        <div className="p-grid">
            <div className="p-col-12">
                <div className="card p-fluid">
                    <div className="p-grid">
                        <div className="p-col-12 p-md-6">
                            <InputTextController id='partyId' label={t('customer.id')}
                                control={control} getFormErrorMessage={getFormErrorMessage} />

                            <InputTextController id='fullName' label={t('customer.name')} checkErr
                                control={control} getFormErrorMessage={getFormErrorMessage} />
                            
                            <InputTextController id='taxInformation' label={t('customer.taxInformation')}
                                control={control} getFormErrorMessage={getFormErrorMessage} />

                            {/* <DropdownController id='partyTypeEnumId' label={t('customer.type')} checkErr disabled
                                options={partyTypes} control={control} getFormErrorMessage={getFormErrorMessage} /> */}

                            <DropdownController id='productStoreId' label={t('productStore')} checkErr
                                options={listProductStoreId} control={control} getFormErrorMessage={getFormErrorMessage} />

                            <InputTextController id='description' label={t('customer.description')}
                                control={control} getFormErrorMessage={getFormErrorMessage} />
                        </div>

                        <div className="p-col-12 p-md-6">
                            {/* <DropdownController id='currencyUomId' label={t('acc.newArInvoice.currencyUOM')}
                                options={currencies} control={control} getFormErrorMessage={getFormErrorMessage} /> */}

                            <InputTextController id='officeSiteName' label={t('customer.webAddress')}
                                control={control} getFormErrorMessage={getFormErrorMessage} />

                            <DropdownController id='salesmanId' label={t('customer.salesman')}
                                options={emptyList} control={control} getFormErrorMessage={getFormErrorMessage} />

                            <DropdownController id='routeId' label={t('customer.route')}
                                options={emptyList} control={control} getFormErrorMessage={getFormErrorMessage} />

                            <DropdownController id='unitId' label={t('customer.consigneeUnit')}
                                options={emptyList} control={control} getFormErrorMessage={getFormErrorMessage} />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}
