import React, { useEffect, useState, useContext, useCallback } from 'react';

import { useTranslation } from 'react-i18next';

import { TextWithLabel } from '../../../components/LabelWithText';

import { ProductService } from '../../../service/ProductService';
import { CommonService } from '../../../service/CommonService';

import { UserProfileContext } from '../../../App';

export const GeneralInformation = (props) => {
    const { t } = useTranslation('common');
    const userProfile = useContext(UserProfileContext);
    
    const { productId } = props;

    const productService = new ProductService();
    const commonService = new CommonService();

    const [ product, setProduct ] = useState(null);
    const [ price, setPrice ] = useState(null);
    const [ dimension, setDimension ] = useState(null);

    useEffect(() => {
        init();
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const init = useCallback(async () => {
        try {
            const stores = await commonService.getProductStores(userProfile.info.partyId);
        
            let params = {
                productStoreId: stores.productStoreList[0].productStoreId,
                productId: productId
            };
            const data = await productService.getProductDetail(params);
            setProduct(data.product);
            setPrice(data.priceOut);

            const productDimension = await productService.getProductDimensions(params);
            setDimension(productDimension.dimension);
        } catch (error) {
            console.log(error);
        }
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    return(
        <div className="p-grid p-mt-2">
            <div className="p-col-12 p-md-6">
                <h5>{t('product.generalInformation')}</h5>
                <TextWithLabel label={t('product.id')} text={product?.productId} />
                <TextWithLabel label={t('product.type')} text={product?.productTypeEnumId} />
                <TextWithLabel label={t('product.name')} text={product?.productName} />
                <TextWithLabel label={t('product.barcode')} text='' />
                <TextWithLabel label={t('product.brand')} text='' />
                <TextWithLabel label={t('product.productCategory')} text={product?.categoryName} />
                <TextWithLabel label={t('product.status')} text={product?.statusId} />
                <TextWithLabel label={t('product.description')} text={product?.description} />
            </div>
            <div className="p-col-12 p-md-6">
                <h5>{t('product.details')}</h5>
                <TextWithLabel label={t('product.weighUnit')} text={dimension?.uomDescription} />
                <TextWithLabel label={t('product.netWeigh')} text='' />
                <TextWithLabel label={t('product.weigh')} text={dimension?.value} />
                <TextWithLabel label={t('product.baseUnit')} text={dimension?.uomDescription} />
                <TextWithLabel label={t('product.salesUnit')} text={dimension?.uomDescription} />
                <TextWithLabel label={t('product.purchaseUnit')} text={dimension?.uomDescription} />
                <TextWithLabel label={t('product.unitPriceDefault')} text={price?.price + ' (' + price?.priceUomId + ')'} />
            </div>
        </div>
    );
}
