import React, {  useRef } from 'react';
import { useTranslation } from "react-i18next";
import PropTypes from 'prop-types'; 
import { Panel } from 'primereact/panel';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import Moment from 'react-moment';
import CurrencyFormat from 'react-currency-format';


export const SalesOrderPartDetailGeneral = (props) => {

    const {orderPart} = props;

    const dt = useRef(null);
    const { t, i18n } = useTranslation();

    const rowIndexBodyTemplate = (rowData, props) => {
        let index = parseInt(props.rowIndex + 1);
        return (
            <React.Fragment>
                <span>{index}</span>
            </React.Fragment>
        );
    };
    
    const currencyBodyTemplate = (rowData, props) => {
        return (
            <React.Fragment>
                 <CurrencyFormat value={rowData[props.field]} displayType={'text'} thousandSeparator={i18n.language === "vi" ? '.': ','} decimalSeparator={i18n.language === "vi" ? ',': '.'}/>
            </React.Fragment>
        );
    }
    
    return (
        <div>
                <Panel >
                    <div className="p-grid">
                        <div className="p-col-12 p-md-6">
                                {/* <div className="p-d-flex p-ai-center p-jc-between p-py-2">
                                    <span>{t('common:order.orderId')}: </span>
                                    <span className="p-text-bold">{orderPart.orderId}</span>
                                </div> */}
                                <div className="p-d-flex p-ai-center p-jc-between p-py-2">
                                    <span>{t('common:order.status')}: </span>
                                    <span className="p-text-bold">{t(`status:OrderHeader.${orderPart.status.statusId}`)}</span>
                                </div>
                                <div className="p-d-flex p-ai-center p-jc-between p-py-2">
                                    <span>{t('common:order.customer')}: </span>
                                    <span className="p-text-bold">
                                       [{orderPart.customer?.partyId}]
                                       {orderPart.customer?.person?.lastName} {orderPart.customer?.person?.middleName} {orderPart.customer?.person?.firstName}
                                       {orderPart.customer?.organization?.organizationName}
                                    </span>
                                </div>
                                {/* <div className="p-d-flex p-ai-center p-jc-between p-pt-2 p-py-2">
                                    <span>{t('common:order.productStore')}</span>
                                    <span className="p-text-bold">{orderPart.productStore?.productStoreId} {orderPart.productStore?.storeName}</span>
                                </div> */}

                                   <div className="p-d-flex p-ai-center p-jc-between p-pt-2 p-py-2">
                                    <span>{t('common:order.paymentMethod')}</span>
                                    <span className="p-text-bold">
                                        {orderPart.payments?.map((payment) => { return (payment.paymentInstrumentEnumId)})}
                                    </span>
                                </div>

                        </div>
                        <div className="p-col-12 p-md-6">
                                <div className="p-d-flex p-ai-center p-jc-between p-py-2">
                                    <span>{t('common:order.shipBeforeDate')}: </span>
                                    <span className="p-text-bold"><Moment format="DD/MM/YYYY HH:mm">{orderPart.shipBeforeDate}</Moment></span>
                                </div>
                                <div className="p-d-flex p-ai-center p-jc-between p-py-2">
                                    <span>{t('common:order.carrier')}: </span>
                                    <span className="p-text-bold">{orderPart.carrier?.partyId} [{orderPart.shipmentMethod?.enumId} - {orderPart.shipmentMethod?.description}]</span>
                                </div>
                                <div className="p-d-flex p-ai-center p-jc-between p-py-2">
                                    <span>{t('common:order.destPostalAddress')}: </span>
                                    <span className="p-text-bold">
                                        {orderPart.postal?.postalAddress?.address1} 
                                        , {orderPart.postal?.postalAddress?.stateProvinceGeo?.geoNameLocal} 
                                        , {orderPart.postal?.postalAddress?.countryGeo?.geoName} 
                                    </span>
                                </div>
                               
                        </div>
                    </div>
                </Panel>
                
                <DataTable ref={dt} value={orderPart.items} lazy dataKey="orderItemSeqId" selectionMode="single">
                    <Column header="#" body={rowIndexBodyTemplate} headerStyle={{ width: "4rem" }}/>
                    <Column field="productId" header={t('common:order.productId')} ></Column>
                    <Column field="itemDescription" header={t('common:order.itemDescription')} ></Column>
                    <Column field="quantity" header={t('common:order.quantity')} ></Column>
                    <Column field="unitAmount" header={t('common:order.unitAmount')} body={currencyBodyTemplate}></Column>
                </DataTable>
        </div>
    );
}


