import React, {useState, useEffect, useRef} from 'react';
import {DataTable} from 'primereact/datatable';
import {Column} from 'primereact/column';
import {Button} from 'primereact/button';
import Moment from "react-moment";
import {Dialog} from "primereact/dialog";
import {Dropdown} from "primereact/dropdown";
import {Calendar} from "primereact/calendar";
import {getTokenCookie} from "../../TokenCookie";
import {InputNumber} from "primereact/inputnumber";
import {PaymentInService} from "../../service/accounting/PaymentInService";
import {useTranslation} from "react-i18next";
import { ServiceHandle } from '../../utilities';
import Const from "../../utilities/Const";

// DataGrid Thanh toán (nhận tiền). Dialog thêm mới Thanh toán (Nhận tiền).
export const PaymentIn = () => {
    const {t} = useTranslation('common');
    const [payments, setPayments] = useState(null);
    const toast = useRef(null);
    const [dialog, setDialog] = useState(false);
    // const [submitted, setSubmitted] = useState(false);
    const [selectedItem, setSelectedItem] = useState(null);
    const [partiesFrom, setPartiesFrom] = useState(null);
    const [partiesTo, setPartiesTo] = useState(null);
    const [paymentTypes, setPaymentTypes] = useState(null);
    const [loading, setLoading] = useState(false);
    const dt = useRef(null);
    const [value1, setValue1] = useState(''); // party chi tien
    const [value2, setValue2] = useState(''); // party nhan tien
    const [value3, setValue3] = useState(''); // Ngay lap phieu
    const [value4, setValue4] = useState(''); // Phuong thuc thanh toan
    const [value5, setValue5] = useState(''); // So tien

    const [lazyParams, setLazyParams] = useState({
        first: 0,
        rows: 10,
        page: 0,
        sortField: "deliveryClusterId",
        sortOrder: -1
    });
    let loadLazyTimeout = null;
    const [totalRecords, setTotalRecords] = useState(0);
    // const enumService = new EnumService();
    const paymentInService = new PaymentInService();

    useEffect(() => {
        loadLazyData();
        paymentInService.getPaymentTypes().then(data => {
            setPaymentTypes(data);
        });
        paymentInService.getListPartyIdFrom().then(data => {
            setPartiesFrom(data.partyIdList);
        });
        paymentInService.getListPartyIdTo().then(data => {
            setPartiesTo(data.partyIdList);
        });
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [lazyParams]);

    const loadLazyData = () => {
        setLoading(true);
        if (loadLazyTimeout) {
            clearTimeout(loadLazyTimeout);
        }
        loadLazyTimeout = setTimeout(() => {
            paymentInService.getPayments(transformRequest()).then(data => {
                setTotalRecords(data.totalRecords);
                setPayments(data.paymentList);
                setLoading(false);
            });
        }, Math.random() * 1000 + 250);
    }

    const transformRequest = () => {
        let params = {
            pageIndex: lazyParams.page,
            pageSize: lazyParams.rows
        }
        if(lazyParams.sortOrder){
            params['orderByField'] = (lazyParams.sortOrder === -1? "-":"") + lazyParams.sortField
        }
        if(lazyParams.filters){
            for (let [key, value] of Object.entries(lazyParams.filters)) {
                params[key] = value.value ;
            }
        }
        return params;
    }

    // const tableHeader = (
    //     <div className="table-header">
    //         {t('acc.paymentIn.title')}
    //     </div>
    // );

    const onPage = (event) => {
        let _lazyParams = { ...lazyParams, ...event };
        setLazyParams(_lazyParams);
    }

    const onSort = (event) => {
        let _lazyParams = { ...lazyParams, ...event };
        setLazyParams(_lazyParams);
    }

    const bodyTemplate = (data, props) => {
        return (
            <>
                {data[props.field]}
            </>
        );
    };

    const timeTemplate = (data, props) => {
        return (
            <>
                <Moment format="DD/MM/YYYY">{data[props.field] ? data[props.field] : ""}</Moment>
            </>
        );
    };

    const savePaymentIn = () => {
        // setSubmitted(true);
        let input = null;
        input = {
            fromPartyId: value1.partyId,
            toPartyId: value2.partyId,
            effectiveDate: value3.toISOString(),
            paymentTypeEnumId: value4.enumId,
            amount: value5
        };
        ServiceHandle.post(Const.API.PaymentCreateNew,
            input,
            {
                headers: {
                    moquiSessionToken: getTokenCookie()
                }
            },
        )
            .then(function (response) {
                // console.log(response);
            })
            .catch(function (error) {
                // console.log(error);
            });
        toast.current.show({severity: 'success', summary: t('acc.paymentIn.noticeHeader'), detail: t('acc.paymentIn.noticeSuccess'), life: 3000});
        setDialog(false);
    }

    const hideDialog = () => {
        // setSubmitted(false);
        setDialog(false);
    }

    const dialogFooter = (
        <>
            <Button label={t('acc.paymentIn.btnCancel')} icon="pi pi-times" className="p-button-text" onClick={hideDialog}/>
            <Button label={t('acc.paymentIn.btnSave')} icon="pi pi-check" className="p-button-text" onClick={savePaymentIn}/>
        </>
    );

    const onFilter = (event) => {
        let _lazyParams = { ...lazyParams, ...event };
        _lazyParams['first'] = 0;
        setLazyParams(_lazyParams);
    }

    const openNew = () => {
        setDialog(true);
    }

    // return (
    //     <div>
    //         <div className="p-col-12">
    //             <div className="card">
    //                 <div className="p-grid">
    //                     <div className="p-col-12 p-md-9">
    //                         <h3>{t('acc.paymentIn.title')}</h3>
    //                     </div>
    //                     <div className="p-col-12 p-md-3">
    //                         <div className="p-grid">
    //                             <Button label={t('acc.paymentIn.addNew')} icon="pi pi-plus" className="p-button-outlined" onClick={openNew} /> &nbsp;&nbsp;
    //                             <Button label="Xóa" icon="pi pi-trash" className="p-button-outlined" onClick={null} disabled={!selectedItem || !selectedItem.length}/> &nbsp;&nbsp;
    //                             <Button type="button" label={t("common:clear")} className="p-button-outlined" icon="pi pi-filter-slash" onClick={null} /> &nbsp;&nbsp;
    //                         </div>
    //                     </div>
    //                 </div>
    return (
        <div>
            <div className="p-col-12">
                <div className="card">
                    <div className="p-grid">
                        <div className="p-col-12 p-md-9">
                            <h3>{t('acc.paymentIn.title')}</h3>
                        </div>
                        <div className="p-col-12 p-md-3">
                            <div className="p-grid">
                                <Button label={t('acc.paymentIn.addNew')} icon="pi pi-plus" className="p-button-outlined" onClick={openNew} /> &nbsp;&nbsp;
                                <Button label="Xóa" icon="pi pi-trash" className="p-button-outlined" onClick={null} disabled={!selectedItem}/> &nbsp;&nbsp;
                                <Button type="button" label={t("common:clear")} className="p-button-outlined" icon="pi pi-filter-slash" onClick={null} /> &nbsp;&nbsp;
                            </div>
                        </div>
                    </div>
                    <DataTable ref={dt} value={payments} paginator className="p-datatable-customers"
                               dataKey="paymentId" emptyMessage={t('acc.paymentIn.NoReceivableInvoiceFound')}
                               loading={loading} onSelectionChange={(e) => setSelectedItem(e.value)} filters
                               onFilter={onFilter} first={lazyParams.first} rows={lazyParams.rows}
                               totalRecords={totalRecords} onPage={onPage} rowsPerPageOptions={[10, 25, 50, 100]}
                               paginatorTemplate="FirstPageLink PrevPageLink PageLinks NextPageLink LastPageLink CurrentPageReport RowsPerPageDropdown"
                               currentPageReportTemplate="Hiển thị {first} - {last} trong tổng số {totalRecords} bản ghi" lazy
                               onSort={onSort} sortField={lazyParams.sortField} sortOrder={lazyParams.sortOrder}
                               rowHover resizableColumns columnResizeMode="fit" reorderableColumns showGridlines>
                        <Column field="paymentId" header={t('acc.paymentIn.paymentId')} sortable body={bodyTemplate} filter/>
                        <Column field="paymentTypeEnumId" header={t('acc.paymentIn.paymentTypeEnumId')} sortable body={bodyTemplate} filter/>
                        <Column field="paymentMethodId" header={t('acc.paymentIn.paymentMethodId')} sortable body={bodyTemplate} filter/>
                        <Column field="paymentInstrumentEnumId" header={t('acc.paymentIn.paymentInstrumentEnumId')} sortable body={bodyTemplate} filter/>
                        <Column field="fromPartyId" header={t('acc.paymentIn.fromPartyId')} sortable body={bodyTemplate} filter/>
                        <Column field="toPartyId" header={t('acc.paymentIn.toPartyId')} sortable body={bodyTemplate} filter/>
                        <Column field="toPaymentMethodId" header={t('acc.paymentIn.toPaymentMethodId')} sortable body={bodyTemplate} filter/>
                        <Column field="paymentGatewayConfigId" header={t('acc.paymentIn.paymentGatewayConfigId')} sortable body={timeTemplate} filter/>
                    </DataTable>
                </div>
            </div>

            <Dialog visible={dialog} style={{width: '450px'}} header={t('acc.paymentIn.paymentInAddHeader')} modal className="p-fluid" footer={dialogFooter} onHide={hideDialog}>
                <div className="p-field">
                    <label htmlFor="d1">{t('acc.paymentIn.paymentInAddFromParty')}<span className="asterisk-require">*</span></label>
                    <Dropdown id="d1" options={partiesFrom} value={value1} onChange={(e) => setValue1(e.value)} appendTo={document.body}></Dropdown>
                </div>
                <div className="p-field">
                    <label htmlFor="d2">{t('acc.paymentIn.paymentInAddToParty')}<span className="asterisk-require">*</span></label>
                    <Dropdown id="d2" options={partiesTo} value={value2} onChange={(e) => setValue2(e.value)} appendTo={document.body}></Dropdown>
                </div>
                <div className="p-field">
                    <label htmlFor="c1">{t('acc.paymentIn.paymentInAddVoucherDate')}<span className="asterisk-require">*</span></label>
                    <Calendar id="c1" dateFormat={"dd/mm/yy"} showIcon value={value3} onChange={(e) => setValue3(e.value)} appendTo={document.body}></Calendar>
                </div>
                <div className="p-field">
                    <label htmlFor="d4">{t('acc.paymentIn.paymentInAddPaymentMethod')}<span className="asterisk-require">*</span></label>
                    <Dropdown id="d4" options={paymentTypes} value={value4} onChange={(e) => setValue4(e.value)} optionLabel="description" appendTo={document.body}></Dropdown>
                </div>
                <div className="p-field">
                    <label htmlFor="i1">{t('acc.paymentIn.paymentInAddMoneyAmount')}<span className="asterisk-require">*</span></label>
                    <InputNumber id="i1" value={value5} keyfilter="num" onChange={(e) => setValue5(e.value)}/>
                </div>
            </Dialog>
        </div>
    )
}
