import React, { useState, useEffect, useRef } from 'react';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import {useTranslation} from "react-i18next";
import Moment from "react-moment";
import {TimePeriodService} from "../../service/accounting/TimePeriodService";
import {Button} from "primereact/button";

export const TimePeriod = () => {
    const {t} = useTranslation('common');
    const [tableData, setTableData] = useState(null);
    // const [loading, setLoading] = useState(false);
    const dt = useRef(null);
    const [selectedItem, setSelectedItem] = useState(null);

    const onSort = (event) => {
        let _lazyParams = { ...lazyParams, ...event };
        setLazyParams(_lazyParams);
    }

    const [lazyParams, setLazyParams] = useState({
        first: 0,
        rows: 10,
        page: 0,
        sortField: "deliveryClusterId",
        sortOrder: -1
    });
    // let loadLazyTimeout = null;
    const [totalRecords, setTotalRecords] = useState(0);
    // const enumService = new EnumService();

    useEffect(() => {
        const timePeriodService = new TimePeriodService();
        timePeriodService.getTimePeriods().then(data => {
            setTableData(data);
            setTotalRecords(data.length);
            // setTotalRecords(data.totalRecords);
        });
    }, []);

    const onFilter = (event) => {
        let _lazyParams = { ...lazyParams, ...event };
        _lazyParams['first'] = 0;
        setLazyParams(_lazyParams);
    }

    const onPage = (event) => {
        let _lazyParams = { ...lazyParams, ...event };
        setLazyParams(_lazyParams);
    }

    // const tableHeader = (
    //     <div className="table-header">
    //         {t('acc.timePeriod.title')}
    //     </div>
    // );

    const bodyTemplate = (data, props) => {
        return (
            <>
                {data[props.field]}
            </>
        );
    };

    const timeTemplate = (data, props) => {
        return (
            <>
                <Moment format="DD/MM/YYYY">{data[props.field] ? data[props.field] : ""}</Moment>
            </>
        );
    };

    return (
        <div>
            <div className="p-col-12">
                {/*<Toast ref={toast}/>*/}
                <div className="card">
                    <div className="p-grid">
                        <div className="p-col-9 p-md-9">
                            {/*<h3>{t('acc.trans.dataGridHeader')}</h3>*/}
                            <h3>Kỳ kế toán</h3>
                        </div>
                        <div className="p-col-3 p-md-3">
                            <div className="p-grid">
                                <Button label={t('acc.trans.addNew')} icon="pi pi-plus" className="p-button-outlined" onClick={null} />&nbsp;&nbsp;
                                <Button label={t('acc.invReceivable.btnDelete')} icon="pi pi-trash" className="p-button-outlined" onClick={null} disabled={!selectedItem || !selectedItem.length}/>&nbsp;&nbsp;
                                <Button type="button" label={t("common:clear")} className="p-button-outlined" icon="pi pi-filter-slash" onClick={null} />
                            </div>
                        </div>
                    </div>
                    <DataTable ref={dt} value={tableData} paginator className="p-datatable-customers"
                               dataKey="deliveryClusterId" emptyMessage={t('acc.timePeriod.noTimePeriodFound')}
                               selectionMode="single" onSelectionChange={(e) => setSelectedItem(e.value)}
                               filters onFilter={onFilter} first={lazyParams.first} rows={lazyParams.rows}
                               totalRecords={totalRecords} onPage={onPage} rowsPerPageOptions={[10, 25, 50, 100]}
                               paginatorTemplate="FirstPageLink PrevPageLink PageLinks NextPageLink LastPageLink CurrentPageReport RowsPerPageDropdown"
                               currentPageReportTemplate="Hiển thị {first} - {last} trong tổng số {totalRecords} bản ghi"
                               lazy onSort={onSort} sortField={lazyParams.sortField} sortOrder={lazyParams.sortOrder}
                               rowHover resizableColumns columnResizeMode="fit" reorderableColumns showGridlines>
                        <Column field="timePeriodId" header={t('acc.timePeriod.timePeriodId')} sortable body={bodyTemplate} filter/>
                        <Column field="parentPeriodId" header={t('acc.timePeriod.parentPeriodId')} sortable body={bodyTemplate} filter/>
                        <Column field="previousPeriodId" header={t('acc.timePeriod.previousPeriodId')} sortable body={bodyTemplate} filter/>
                        <Column field="timePeriodTypeId" header={t('acc.timePeriod.timePeriodTypeId')} sortable body={bodyTemplate} filter/>
                        <Column field="partyId" header={t('acc.timePeriod.partyId')} sortable body={timeTemplate} filter/>
                        <Column field="periodNum" header={t('acc.timePeriod.periodNum')} sortable body={bodyTemplate} filter/>
                        <Column field="periodName" header={t('acc.timePeriod.periodName')} sortable body={bodyTemplate} filter/>
                    </DataTable>
                </div>
            </div>
        </div>
    )
}
