import React, { useState, useEffect, useRef } from 'react';
import { useTranslation } from "react-i18next";
import PropTypes from 'prop-types'; 
import { Toast } from 'primereact/toast';
import { TabView, TabPanel } from 'primereact/tabview';

import { InventoryDetailHistoryList } from './InventoryDetailHistoryList';
import { InventoryDetailInfo } from './InventoryDetailInfo';

import { AssetService } from '../../service/AssetService';

export const InventoryDetail = (props) => {

    const {assetId}=props
    let emptyAsset = {
        "assetId": "",
        "assetName": "",
        "productId": "",
        "hasQuantity": "Y",
        "acquiredDate": "",
        "quantityOnHandTotal": "",
        "availableToPromiseTotal": "",
        "originalQuantity": "",
        "product": {},
        "type": {},
        "facility": {},
        "owner": {},
        "lot": {},
        "details": [],
        "receipts": [],
        "reservations": [],
        "status": {}
    };

    const [asset, setAsset] = useState(emptyAsset);
    const toast = useRef(null);
    const { t } = useTranslation();


    useEffect(() => {
        const assetService = new AssetService();

        const getAsset = async()=>{
            const response1 = await assetService.getAsset(assetId)
            const response2 = await assetService.getAssetDisplayInfo(assetId)
            let _asset = {...response1, ...response2};
            setAsset(_asset);
        }
         getAsset();
    },[assetId]);
    
    return (
        <div>
            <div className="card">
                <Toast ref={toast} />
                <TabView>
                        <TabPanel header={t('common:inventory.basicInfo')}>
                            <InventoryDetailInfo  asset={asset} />
                        </TabPanel>
                        <TabPanel header={t('common:inventory.inventoryDetailList')}>
                        <InventoryDetailHistoryList assetDetailList={asset.details}/>
                        </TabPanel>
                    </TabView>
            </div>
        </div>
    );
};

InventoryDetail.propTypes  = {
    assetId: PropTypes.string.isRequired
};
