import React, { useEffect, useRef, useState, useCallback } from 'react';

import { useTranslation } from 'react-i18next';
import moment from 'moment';

import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';

import { ProductService } from '../../../service/ProductService';

export const Price = (props) => {
    const { t } = useTranslation();
    
    const { productId } = props;

    const productService = new ProductService();

    const dt = useRef(null);
    const [ basePrice, setBasePrice ] = useState(null);
    const [ settingPrice, setSettingPrice ] = useState(null);

    useEffect(() => {
        init();
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const init = useCallback(async () => {
        let params = {
            productId: productId
        };
        const data = await productService.getProductPrice(params);
        
        setBasePrice(data.productPrice.filter((elm) => !elm.productStoreId));

        setSettingPrice(data.productPrice.filter((elm) => elm.productStoreId));
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const bodyTemplate = useCallback((data, props) => {
        return (
            <>
                <span className="p-column-title">{props.header}</span>
                {data[props.field]}
            </>
        );
    }, []);

    const bodyPriceTypeTemplate = useCallback((data, props) => {
        return (
            <>
                <span className="p-column-title">{props.header}</span>
                {t('enum:ProductPricePurpose.' + data[props.field])}
            </>
        );
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const bodyDateTemplate = useCallback((data, props) => {
        return (
            <>
                <span className="p-column-title">{props.header}</span>
                {data[props.field] ? moment(data[props.field]).format('DD/MM/YYYY HH:mm:ss') : ''}
            </>
        );
    }, []);

    return(
        <>
            <div className="card">
                <div className="p-grid p-mt-2 table-demo">
                    <div className="p-col-12 p-md-12">
                        <h5>{t('common:product.basePrice')}</h5>
                        <DataTable value={basePrice} className="p-datatable-customers" dataKey="productId" ref={dt}>
                            <Column field="pricePurposeEnumId" header={t('common:productPrice.type')} body={bodyPriceTypeTemplate}></Column>
                            <Column field="price" header={t('common:product.price')} body={bodyTemplate}></Column>
                            <Column field="priceUomId" header={t('common:acc.newArInvoice.currencyUOM')} body={bodyTemplate}></Column>
                            <Column field="fromDate" header={t('common:productPrice.fromDate')} body={bodyDateTemplate}></Column>
                            <Column field="thruDate" header={t('common:productPrice.thruDate')} body={bodyDateTemplate}></Column>
                        </DataTable>
                    </div>
                </div>
            </div>
            <div className="card">
                <div className="p-grid p-mt-2 table-demo">
                    <div className="p-col-12 p-md-12">
                        <h5>{t('pcommon:product.settingPrice')}</h5>
                        <DataTable value={settingPrice} className="p-datatable-customers" dataKey="productId" ref={dt}>
                        <Column field="pricePurposeEnumId" header={t('common:productPrice.type')} body={bodyPriceTypeTemplate}></Column>
                            <Column field="price" header={t('common:product.price')} body={bodyTemplate}></Column>
                            <Column field="priceUomId" header={t('common:acc.newArInvoice.currencyUOM')} body={bodyTemplate}></Column>
                            <Column field="fromDate" header={t('common:productPrice.fromDate')} body={bodyDateTemplate}></Column>
                            <Column field="thruDate" header={t('common:productPrice.thruDate')} body={bodyDateTemplate}></Column>
                        </DataTable>
                    </div>
                </div>
            </div>
        </>
    );
}
