import React from 'react';
import { Chart } from 'primereact/chart';

export const BarChart = (props) => {
    const { data } = props

    const getLightTheme = () => {
        let basicOptions = {
            maintainAspectRatio: false,
            aspectRatio: .8,
            plugins: {
                legend: {
                    labels: {
                        color: '#495057'
                    }
                }
            },
            scales: {
                x: {
                    ticks: {
                        color: '#495057'
                    },
                    grid: {
                        color: '#ebedef'
                    }, 
                    barPercentage: 0.4
                },
                y: {
                    ticks: {
                        color: '#495057'
                    },
                    grid: {
                        color: '#ebedef'
                    }
                }
            }
        };

        return {
            basicOptions
        }
    }

    const { basicOptions, } = getLightTheme();

        return (
            <Chart type="bar" data={data} options={basicOptions} {...props} />
        )
    }
