import React, { useCallback, useEffect, useState } from 'react';

import { useTranslation } from 'react-i18next';

import { SalesReportService } from '../../service/SalesReportService';

export const StatisticSalesInWeek = (props) => {
    const [ t ] = useTranslation('common');

    const [data, setData] = useState([]);

    const iconStyle = {fontSize: '5rem', color: '#FFFFFF'};
    const colorWhite = {color: '#FFFFFF'};

    const init = useCallback(async () => {
        let endDate = new Date();
        let day = endDate.getDay();
        let diff = endDate.getDate() - day + (day === 0 ? -6 : 1);
        let startDate = new Date(endDate.setDate(diff));

        let params = {
            startDate: startDate.toISOString().slice(0,10), 
            endDate: endDate.toISOString().slice(0,10),
        }

        const salesReportService = new SalesReportService();

        let res = await salesReportService.olapChartTxtForWeek(params);
        setData(res);
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    useEffect(() => {
        init();
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    return (
        <div className="p-grid flexbox-demo height-100">
            <div className="p-col-12">
                <div className="p-d-flex p-flex-column">
                    <div className="p-mb-2 p-text-left width-100" style={{height: '8rem', backgroundColor: '#70B3E0'}}>
                        <div className="p-grid">
                            <div className="p-col-12 p-md-3">
                                <i className="pi pi-chart-bar" style={iconStyle}></i>
                            </div>
                            <div className="p-col-12 p-md-9" style={colorWhite}>
                                <h4 className="p-mt-0">{t('report.salesThisWeek')}</h4>
                                <h4 className="p-mt-0">{data?.grandTotal?.toString() + ' ' + data?.currencyUomId}</h4>
                            </div>
                        </div>
                    </div>
                    <div className="p-mb-2 p-text-left width-100" style={{height: '8rem', backgroundColor: '#1A926C'}}>
                        <div className="p-grid">
                            <div className="p-col-12 p-md-3">
                                <i className="pi pi-chart-line" style={iconStyle}></i>
                            </div>
                            <div className="p-col-12 p-md-9" style={colorWhite}>
                                <h4 className="p-mt-0">{t('report.maxSalesThisWeek')}</h4>
                                <h4 className="p-mt-0">{
                                    data?.bestSelling?.grandTotal ? 
                                        data?.bestSelling?.grandTotal?.toString() + ' ' + data?.currencyUomId : '0'
                                }</h4>
                            </div>
                        </div>
                    </div>
                    <div className="p-text-left width-100" style={{height: '8rem', backgroundColor: '#D68CDF'}}>
                        <div className="p-grid">
                            <div className="p-col-12 p-md-3">
                                <i className="pi pi-shopping-cart" style={iconStyle}></i>
                            </div>
                            <div className="p-col-12 p-md-9" style={colorWhite}>
                                <h4 className="p-mt-0">{t('report.orderThisWeek')}</h4>
                                <h4 className="p-mt-0">{data?.orders}</h4>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};
