import React, { useContext, useState, useEffect, useCallback } from 'react';
import { useTranslation } from 'react-i18next';
import { GoogleMap, LoadScript, Marker, StandaloneSearchBox } from '@react-google-maps/api';

import { InputTextController } from '../../../components/PromotionComponent/InputTextController';
import { DropdownController } from '../../../components/PromotionComponent/DropdownController';
import { Loading } from '../../../components/Loading';

import { GeoService } from '../../../service/GeoService';
import { CustomerService } from '../../../service/CustomerService';
import { ToastContext } from '../../../App';

import { Const } from '../../../utilities';

export const ContactInformation = (props) => {
    const { control, errors, setValue, partyId } = props;
    const [ t ] = useTranslation('common');
    const toast = useContext(ToastContext);
    const geoService = new GeoService();
    const customerService = new CustomerService();

    const [ center, setCenter ] = useState(null);
    const [ searchBox, setSearchBox ] = useState(null);
    const [ countries, setCountries ] = useState([]);
    const [ stateProvinces, setStateProvinces ] = useState([]); 
    const [ districts, setDistricts ] = useState([]); 
    const [ wards, setWards ] = useState([]); 

    const [loading, setLoading] = useState(false);
    
    const libraries = ['places'];
    const getFormErrorMessage = (name) => {
        return errors[name] && <small className="p-error">{errors[name]?.message}</small>
    };

    useEffect(() => {
        init();
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    useEffect(() => {
        if (center?.lat && center?.lng) {
            setValue('latitude', center?.lat);
            setValue('longitude', center?.lng);

            // getPosition(center?.lat, center?.lng);
        }
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [center?.lat, center?.lng]);

    const setPosition = useCallback(async (addComponents) => {
        let n = addComponents.length;

        // set country from current location
        let countryName = addComponents[n - 1] === 'Vietnam' ? 'Việt Nam' : addComponents[n - 1];
        let country = countries.find(elm => elm.name === countryName);
        setValue('countryGeoId', country);
        
        let data = await geoService.getGeoRegionList(country.code, []);
        let newStateProvinces = data.map(elm => {return {name: elm.geoName, code: elm.geoId}});
        setStateProvinces(newStateProvinces);

        // set stateProvinces from current location
        let stateProvinceName = addComponents[n - 2];
        let stateProvince = newStateProvinces.find(elm => elm.name === stateProvinceName);
        setValue('stateProvinceGeoId', stateProvince);

        data = await geoService.getGeoRegionList(stateProvince.code, []);
        let newDistricts = data.map(elm => {return {name: elm.geoName, code: elm.geoId}});
        setDistricts(newDistricts);

        // set district from current location
        let districtName = addComponents[n - 3];
        let district = newDistricts.find(elm => elm.name === districtName);
        setValue('districtId', district);

        data = await geoService.getGeoRegionList(district.code, []);
        let newWards = data.map(elm => {return {name: elm.geoName, code: elm.geoId}});
        setWards(newWards);

        // set ward from current location
        let wardName = addComponents[n - 4];
        let ward = newWards.find(elm => elm.name === wardName);
        setValue('wardId', ward);

        let tarAddress = '';
        for (let i = 0; i < n - 4; i++) {
            tarAddress += addComponents[i] + ' '
        }
        setValue('tarAddress', tarAddress);
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [countries]);

    const init = useCallback(async () => {
        let data = await geoService.getCountries([]);
        setCountries(data.geoList.map((elm) => {
            return {
                name: elm.geoName,
                code: elm.geoId
            }
        }));

        let params = {
            partyId: partyId
        }
        data = await customerService.getPersonalCustomerDetail(params);

        if (data?.person) {
            // eslint-disable-next-line no-use-before-define
            let temp = data.person;

            if (temp.latitude && temp.longitude) {
                setCenter({lat: temp.latitude, lng: temp.longitude});
            }

            // set country from current location
            let country = countries.find(elm => elm.code === temp.countryGeoId)
            setValue('countryGeoId', country);

            let data = await geoService.getGeoRegionList(country.code, []);
            let newStateProvinces = data.map(elm => {return {name: elm.geoName, code: elm.geoId}});
            setStateProvinces(newStateProvinces);

            // set stateProvinces from current location
            let stateProvince = newStateProvinces.find(elm => elm.code === temp.stateProvinceGeoId);
            setValue('stateProvinceGeoId', stateProvince);

            data = await geoService.getGeoRegionList(stateProvince.code, []);
            let newDistricts = data.map(elm => {return {name: elm.geoName, code: elm.geoId}});
            setDistricts(newDistricts);

            // set district from current location
            let district = newDistricts.find(elm => elm.code === temp.countyGeoId);
            setValue('districtId', district);

            data = await geoService.getGeoRegionList(district.code, []);
            let newWards = data.map(elm => {return {name: elm.geoName, code: elm.geoId}});
            setWards(newWards);

            // set ward from current location
            let ward = newWards.find(elm => elm.code === temp.wardId);
            setValue('wardId', ward);

            setValue('tarAddress', temp.postalAddress);

            setValue('telecomNumber', temp.comments);
            setValue('emailAddress', temp.comments);

            setValue('latitude', temp.latitude);
            setValue('longitude', temp.longitude);

            let productStore = {
                name: temp.productStoreId,
                code: temp.productStoreId
            }
            setValue('productStoreId', productStore);
        }
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const changeCountry = useCallback(async (e) => {
        let data = await geoService.getGeoRegionList(e.value.code, []);
        setStateProvinces(data.map((elm) => {
            return {
                name: elm.geoName,
                code: elm.geoId
            }
        }));
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const changeStateProvinces = useCallback(async (e) => {
        let data = await geoService.getGeoRegionList(e.value.code, []);
        setDistricts(data.map((elm) => {
            return {
                name: elm.geoName,
                code: elm.geoId
            }
        }));
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const changeDistrict = useCallback(async (e) => {
        let data = await geoService.getGeoRegionList(e.value.code, []);
        setWards(data.map((elm) => {
            return {
                name: elm.geoName,
                code: elm.geoId
            }
        }));
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const onPlacesChanged = useCallback(() => {
        setLoading(true);
        try {
            let formattedAddress = searchBox.getPlaces()[0].formatted_address;
            let addComponents = formattedAddress.split(', ');

            setCenter({lat: searchBox.getPlaces()[0].geometry.location.lat(), lng: searchBox.getPlaces()[0].geometry.location.lng()});
            setPosition(addComponents);
        } catch (error) {
            toast.current.show({ severity: 'error', summary: t('errorMessage'), detail: error, life: 5000 });
        } finally {
            setLoading(false);
        }
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [countries, searchBox]);

    return (
        <div className="p-grid">
            <div className="p-col-12">
                <div className="card p-fluid">
                    <div className="p-grid">
                        <div className="p-col-12 p-md-6">
                            <DropdownController id='countryGeoId' label={t('customer.countryGeoId')} checkErr callback={changeCountry} 
                                options={countries} control={control} getFormErrorMessage={getFormErrorMessage} />

                            <DropdownController id='stateProvinceGeoId' label={t('customer.stateProvinceGeoId')} checkErr callback={changeStateProvinces} 
                                options={stateProvinces} control={control} getFormErrorMessage={getFormErrorMessage} />

                            <DropdownController id='districtId' label={t('customer.districtId')} checkErr callback={changeDistrict} 
                                options={districts} control={control} getFormErrorMessage={getFormErrorMessage} />

                            <DropdownController id='wardId' label={t('customer.wardId')} checkErr 
                                options={wards} control={control} getFormErrorMessage={getFormErrorMessage} />

                            <InputTextController id='tarAddress' label={t('customer.tarAddress')} checkErr
                                control={control} getFormErrorMessage={getFormErrorMessage} />
                        </div>

                        <div className="p-col-12 p-md-6">
                            <InputTextController id='telecomNumber' label={t('customer.telecomNumber')}
                                control={control} getFormErrorMessage={getFormErrorMessage} />
                            
                            <InputTextController id='emailAddress' label={t('customer.emailAddress')}
                                control={control} getFormErrorMessage={getFormErrorMessage} />
                        </div>
                    </div>
                    <div className="p-grid">
                        <LoadScript googleMapsApiKey={Const.GGMAPS.key} libraries={libraries}> 
                            <GoogleMap
                                mapContainerStyle={{width: '100%', height: '400px'}}
                                center={center ? center : {lat: 20.9934632, lng: 105.7865466}}
                                zoom={15}
                            >
                                {center &&<Marker position={center} />}

                                <StandaloneSearchBox onLoad={ref => setSearchBox(ref)} onPlacesChanged={onPlacesChanged}>
                                    <input type="text" placeholder={t('search')}
                                        style={{
                                            boxSizing: `border-box`, border: `1px solid transparent`,
                                            width: `30em`, height: `3em`, padding: `0 12px`,
                                            borderRadius: `3px`, boxShadow: `0 2px 6px rgba(0, 0, 0, 0.3)`,
                                            fontSize: `14px`, outline: `none`, textOverflow: `ellipses`,
                                            position: "absolute", left: "40%", marginLeft: "-8em", marginTop: "1em"
                                        }}
                                    />
                                </StandaloneSearchBox>
                            </GoogleMap>
                        </LoadScript>
                    </div>
                </div>
            </div>
            <Loading visible={loading} onHide={() => setLoading(false)} />
        </div>
    );
}
